// jscs:disable requireMultipleVarDecl
/* global require console setTimeout */

var SimpleProxy = require("./simple-proxy.js");
var proxyServer = new SimpleProxy(8080, console);

proxyServer.start();

setTimeout(function () {
    "use strict";
    proxyServer.stop();
    setTimeout(function () {
            proxyServer.start();
            setTimeout(function () {
                    proxyServer.stop();
                }, 20000);
        }, 20000);
}, 20000);
